const data = $('GetColumns').item.json;
const { eventType, itemData, targetColumn, columns, widgets, repository, targetArea } = data;

console.log(`üì¶ Movendo ${eventType} #${itemData.number} para "${targetColumn}"`);
console.log(`üìÅ Reposit√≥rio: ${repository} (√°rea: ${targetArea.areaName})`);

const targetCol = columns[targetColumn];
if (!targetCol) {
  throw new Error(`‚ùå Coluna "${targetColumn}" n√£o encontrada na √°rea ${targetArea.areaName}`);
}

const githubId = eventType === 'issue' ? 
  `[${repository}:GHID:${itemData.number}]` : 
  `[${repository}:GHPR:${itemData.number}]`;

console.log(`üîç Procurando sticky com ID: "${githubId}"`);

const area = data.area;
let existingItem = widgets.find(w => {
  if (w.type !== 'sticky note' || !w.text || !w.text.includes(githubId)) {
    return false;
  }

  return w.x >= area.x && 
         w.x <= area.x + area.width && 
         w.y >= area.y && 
         w.y <= area.y + area.height;
});

if (!existingItem) {
  console.log(`‚ö†Ô∏è N√£o encontrado na √°rea, tentando busca global...`);

  existingItem = widgets.find(w => 
    w.type === 'sticky note' && 
    w.text && 
    w.text.includes(githubId)
  );

  if (existingItem) {
    console.log(`‚úÖ Sticky encontrado fora da √°rea esperada!`);
    console.log(`   - Widget ID: ${existingItem.id}`);
    console.log(`   - Posi√ß√£o: (${existingItem.x}, ${existingItem.y})`);
    console.log(`   - ParentId: ${existingItem.parentId || 'nenhum'}`);

    if (existingItem.parentId && existingItem.parentId !== targetArea.areaId) {
      console.log(`   ‚ö†Ô∏è Sticky est√° em outra √°rea: ${existingItem.parentId}`);
    }
  }
}

if (!existingItem) {
  console.log(`‚ùå ${eventType} #${itemData.number} n√£o encontrado em lugar nenhum - criando novo`);

  const emoji = eventType === 'issue' ? 'üìù' : 'üîÄ';
  const itemType = eventType === 'issue' ? 'Issue' : 'PR';
  const title = itemData.title.length > 40 ? 
    itemData.title.substring(0, 40) + '...' : 
    itemData.title;

  let stickyText = `${emoji} ${itemType} #${itemData.number}: ${title}\n`;
  stickyText += `üìÅ ${repository}\n`;
  stickyText += `üë§ ${itemData.user.login}\n`;
  stickyText += `üìÖ ${new Date(itemData.created_at).toLocaleDateString('pt-BR')}\n`;

  if (eventType === 'pr') {
    stickyText += `üîÄ ${itemData.head.ref} ‚Üí ${itemData.base.ref}\n`;
  }

  if (eventType === 'issue' && itemData.labels && itemData.labels.length > 0) {
    const labelNames = itemData.labels.slice(0, 2).map(l => l.name).join(', ');
    stickyText += `üè∑Ô∏è ${labelNames}\n`;
  }

  stickyText += `\n${githubId}`;

  const existingStickies = widgets.filter(w => {
    if (w.type !== 'sticky note') return false;

    const inColumn = w.x >= targetCol.contentX && 
                     w.x <= targetCol.contentX + targetCol.contentWidth &&
                     w.y >= targetCol.contentY;

    const inArea = w.x >= area.x && 
                   w.x <= area.x + area.width && 
                   w.y >= area.y && 
                   w.y <= area.y + area.height;

    return inColumn && inArea;
  });

  let nextY = targetCol.contentY + 20;
  if (existingStickies.length > 0) {
    const lowestY = Math.max(...existingStickies.map(s => s.y + (s.height || 150)));
    nextY = lowestY + 20;
  }

  const centerX = targetCol.contentX + (targetCol.contentWidth / 2) - 125;

  return {
    json: {
      action: 'CREATE',
      data: {
        text: stickyText,
        x: centerX,
        y: nextY,
        width: 250,
        height: 170,
        shape: "rectangle",
        parentId: targetArea.areaId
      }
    }
  };
}

console.log(`‚úÖ ${eventType} #${itemData.number} encontrado - ID: ${existingItem.id}`);
console.log(`üìç Posi√ß√£o atual: (${existingItem.x}, ${existingItem.y})`);

if (existingItem.parentId !== targetArea.areaId) {
  console.log(`üîß Ajustando parentId de ${existingItem.parentId || 'nenhum'} para ${targetArea.areaId}`);
}

const existingStickiesInTarget = widgets.filter(w => {
