  if (w.type !== 'shape' || !w.htmlText) return false;

  if (w.parentId === targetArea.areaId) {
    console.log(`   ‚úì Shape encontrada por parentId: "${w.htmlText}"`);
    return true;
  }

  const shapeLeft = w.x;
  const shapeRight = w.x + w.width;
  const shapeTop = w.y;
  const shapeBottom = w.y + w.height;

  const areaLeft = area.x;
  const areaRight = area.x + area.width;
  const areaTop = area.y;
  const areaBottom = area.y + area.height;

  const isInside = shapeLeft >= areaLeft && 
                   shapeRight <= areaRight && 
                   shapeTop >= areaTop && 
                   shapeBottom <= areaBottom;

  if (isInside) {
    console.log(`   ‚úì Shape encontrada por posi√ß√£o: "${w.htmlText}" em (${w.x}, ${w.y})`);
  }

  return isInside;
});

console.log(`üìã Total de shapes dentro da √°rea ${targetArea.areaName}: ${shapes.length}`);

if (shapes.length > 0) {
  console.log(`üìù Shapes encontradas:`);
  shapes.forEach(s => {
    const text = s.htmlText.replace(/<[^>]*>/g, ''); 
    console.log(`   - "${text}" (ID: ${s.id}, Pos: ${s.x},${s.y})`);
  });
} else {
