  const allShapes = widgets.filter(w => w.type === 'shape' && w.htmlText);
  console.log(`üìä Total de shapes no mural: ${allShapes.length}`);
  console.log(`üìç Primeiras 5 shapes para debug:`);
  allShapes.slice(0, 5).forEach(s => {
    const text = s.htmlText.replace(/<[^>]*>/g, '');
    console.log(`   - "${text}" em (${s.x}, ${s.y}) - ParentId: ${s.parentId || 'nenhum'}`);
  });
}

function findColumnShape(targetName) {
  console.log(`\nüîé Procurando shape "${targetName}" na √°rea ${targetArea.areaName}`);

  const shape = shapes.find(s => {
    const htmlLower = s.htmlText.toLowerCase();
    const targetLower = targetName.toLowerCase();
    const found = htmlLower.includes(targetLower);

    if (found) {
      console.log(`   ‚úÖ Encontrada! HTML: "${s.htmlText}"`);
    }

    return found;
  });

  if (!shape) {
    console.log(`   ‚ùå Shape "${targetName}" n√£o encontrada na √°rea ${targetArea.areaName}`);

    const shapeInOtherArea = widgets.find(w => 
      w.type === 'shape' && 
      w.htmlText && 
      w.htmlText.toLowerCase().includes(targetName.toLowerCase())
    );

    if (shapeInOtherArea) {
      console.log(`   ‚ö†Ô∏è Shape "${targetName}" existe, mas fora da √°rea!`);
      console.log(`      Posi√ß√£o: (${shapeInOtherArea.x}, ${shapeInOtherArea.y})`);
      console.log(`      ParentId: ${shapeInOtherArea.parentId || 'nenhum'}`);
    }

    return null;
  }

  const column = {
    name: targetName,
    shapeId: shape.id,
    x: shape.x,
    y: shape.y,
    width: shape.width,
    height: shape.height,
    contentX: shape.x + 50,
    contentY: shape.y + 100,
    contentWidth: shape.width - 100,
    contentHeight: shape.height - 150,
    parentArea: targetArea
  };

  console.log(`   üìê Dimens√µes: ${column.width}x${column.height} em (${column.x}, ${column.y})`);

  return column;
}

const columns = {
  'A Fazer': findColumnShape('A Fazer'),
  'Em Andamento': findColumnShape('Em Andamento'),
  'Em Revis√£o': findColumnShape('Em Revis√£o'),
  'Conclu√≠do': findColumnShape('Conclu√≠do')
};

const foundColumns = Object.values(columns).filter(c => c !== null).length;
console.log(`\nüìä RESUMO - Colunas encontradas na √°rea ${targetArea.areaName}: ${foundColumns}/4`);

Object.entries(columns).forEach(([name, column]) => {
  if (column) {
    console.log(`‚úÖ ${name}: OK`);
  } else {
    console.log(`‚ùå ${name}: N√ÉO ENCONTRADA`);
  }
});

