  json: {
    ...eventData,
    columns: columns,
    widgets: widgets,
    authToken: authToken,
    area: area,
    debug: {
      totalWidgets: widgets.length,
      shapesInArea: shapes.length,
      shapesWithParentId: shapesWithParent.length,
      foundColumns: foundColumns,
      targetColumnExists: targetColumn ? !!columns[targetColumn] : null,
      areaName: targetArea.areaName,
      areaId: targetArea.areaId,
      areaPosition: { x: area.x, y: area.y, width: area.width, height: area.height }
    }
  }
};

const data = $input.item.json;
const event = data.headers?.['x-github-event'];
const action = data.body?.action;
const repository = data.body?.repository?.name;

console.log(`üì• Evento recebido: ${event}.${action} no reposit√≥rio ${repository}`);


const REPO_TO_AREA_MAP = {
  'ESC-APP': {
    areaId: '0-1746550663084', 
    areaName: 'ESC-APP' 
  },
};

const repoConfig = REPO_TO_AREA_MAP[repository];
if (!repoConfig) {
  console.log(`‚ö†Ô∏è Reposit√≥rio "${repository}" n√£o est√° configurado no mapeamento`);
  return { 
    json: { 
      skip: true, 
      reason: `Reposit√≥rio ${repository} n√£o configurado`,
      switchOutput: 0,
      eventKey: `${event}.${action}`
    } 
  };
}

console.log(`üéØ Reposit√≥rio "${repository}" mapeado para √°rea "${repoConfig.areaName}"`);

const eventMap = {
  'issues.opened': { 
    type: 'issue', 
    action: 'create', 
    targetColumn: 'A Fazer',
    switchOutput: 1,
    data: data.body.issue 
  },
  'issues.closed': { 
    type: 'issue', 
    action: 'move', 
    targetColumn: 'Conclu√≠do',
    switchOutput: 2,
    data: data.body.issue 
  },
  'issues.reopened': { 
    type: 'issue', 
    action: 'move', 
    targetColumn: 'A Fazer',
    switchOutput: 2,
    data: data.body.issue 
  },
  'pull_request.opened': { 
    type: 'pr', 
    action: 'create', 
    targetColumn: 'Em Revis√£o',
    switchOutput: 1,
    data: data.body.pull_request 
  },
  'pull_request.closed': {
    type: 'pr',
    action: data.body.pull_request?.merged ? 'complete' : 'skip',
    targetColumn: data.body.pull_request?.merged ? 'Conclu√≠do' : null,
    switchOutput: data.body.pull_request?.merged ? 3 : 0,
    data: data.body.pull_request
  }
};

const eventKey = `${event}.${action}`;
const eventConfig = eventMap[eventKey];

if (!eventConfig || eventConfig.action === 'skip') {
  console.log(`‚è≠Ô∏è Evento ${eventKey} ignorado`);
  return { 
    json: { 
      skip: true, 
      reason: `Evento ${eventKey} n√£o √© relevante para tracking`,
      switchOutput: 0,
      eventKey: eventKey
    } 
  };
}

console.log(`‚úÖ Processando: ${eventConfig.type} ‚Üí ${eventConfig.action} ‚Üí ${eventConfig.targetColumn}`);
console.log(`üéØ √Årea alvo: ${repoConfig.areaName} (${repoConfig.areaId})`);
console.log(`üîÄ Sa√≠da do Switch: ${eventConfig.switchOutput}`);

if (eventConfig.data) {
  console.log(`üìä Item #${eventConfig.data.number}: ${eventConfig.data.title}`);
  console.log(`üë§ Autor: ${eventConfig.data.user?.login}`);
}

return {
  json: {
    eventType: eventConfig.type,
    action: eventConfig.action,
    targetColumn: eventConfig.targetColumn,
    switchOutput: eventConfig.switchOutput,
    itemData: eventConfig.data,
    repository: repository,
    targetArea: repoConfig, 
    timestamp: new Date().toISOString(),
    originalEvent: { 
      event: event, 
      action: action,
      eventKey: eventKey
    },
    skip: false
  }
};

const data = $('GetColumns').item.json;
const { itemData, columns, widgets, repository, targetArea } = data;

const targetCol = columns['Conclu√≠do'];
if (!targetCol) {
  throw new Error(`‚ùå Coluna "Conclu√≠do" n√£o encontrada na √°rea ${targetArea.areaName}`);
}

console.log(`üéâ PR #${itemData.number} merged no reposit√≥rio ${repository}`);
console.log(`üìÅ √Årea: ${targetArea.areaName}`);

const prId = `[${repository}:GHPR:${itemData.number}]`;
const area = data.area;

const prWidget = widgets.find(w => {
  if (w.type !== 'sticky note' || !w.text || !w.text.includes(prId)) {
    return false;
  }

  return w.x >= area.x && 
         w.x <= area.x + area.width && 
         w.y >= area.y && 
         w.y <= area.y + area.height;
});

const prBody = itemData.body || '';
const issueNumbers = [];
const patterns = [
  /(?:closes|fixes|resolves)\s+#(\d+)/gi,
  /(?:close|fix|resolve)\s+#(\d+)/gi
];

patterns.forEach(pattern => {
  let match;
  while ((match = pattern.exec(prBody)) !== null) {
    const issueNum = parseInt(match[1], 10);
    if (!issueNumbers.includes(issueNum)) {
      issueNumbers.push(issueNum);
    }
  }
});

const issueWidgets = [];
issueNumbers.forEach(issueNum => {
  const issueId = `[${repository}:GHID:${issueNum}]`;
  const issueWidget = widgets.find(w => {
    if (w.type !== 'sticky note' || !w.text || !w.text.includes(issueId)) {
      return false;
    }

    return w.x >= area.x && 
           w.x <= area.x + area.width && 
           w.y >= area.y && 
           w.y <= area.y + area.height;
  });

  if (issueWidget) {
    issueWidgets.push({ number: issueNum, widget: issueWidget });
  }
});

console.log(`üîç Issues mencionadas: ${issueNumbers.join(', ')}`);
console.log(`üì¶ Issues encontradas na √°rea ${targetArea.areaName}: ${issueWidgets.length}`);

const existingStickies = widgets.filter(w => {
  if (w.type !== 'sticky note') return false;

  const inColumn = w.x >= targetCol.contentX && 
                   w.x <= targetCol.contentX + targetCol.contentWidth &&
                   w.y >= targetCol.contentY;

  const inArea = w.x >= area.x && 
                 w.x <= area.x + area.width && 
                 w.y >= area.y && 
                 w.y <= area.y + area.height;

  return inColumn && inArea;
});

let nextY = targetCol.contentY + 20;
if (existingStickies.length > 0) {
  const lowestY = Math.max(...existingStickies.map(s => s.y + (s.height || 170)));
  nextY = lowestY + 20;
}

const centerX = targetCol.contentX + (targetCol.contentWidth / 2) - 125;

const moveActions = [];

if (prWidget) {
  moveActions.push({
    type: 'pr',
    widgetId: prWidget.id,
    number: itemData.number,
    x: centerX,
    y: nextY,
    repository: repository
  });
  nextY += 190; 
}

issueWidgets.forEach((issue) => {
  moveActions.push({
    type: 'issue',
    number: issue.number,
    widgetId: issue.widget.id,
    x: centerX,
    y: nextY,
    repository: repository
  });
  nextY += 190; 
});

console.log(`üìã Preparando ${moveActions.length} movimenta√ß√µes para "Conclu√≠do" na √°rea ${targetArea.areaName}`);

moveActions.forEach((action, index) => {
  console.log(`   ${index + 1}. ${action.type} #${action.number} ‚Üí (${action.x}, ${action.y})`);
});

return {
  json: {
    moveActions: moveActions,
    prNumber: itemData.number,
    linkedIssues: issueNumbers,
    targetColumn: 'Conclu√≠do',
    repository: repository,
    targetArea: targetArea
  }
};

const freshWidgets = $('GetFreshWidgets').item.json.value;
const data = $('GetColumns').item.json;
const { eventType, itemData, targetColumn, columns, repository, targetArea } = data;

console.log(`\nüîÑ === INICIANDO RefreshAndMove ===`);
console.log(`üìã Evento: ${eventType} #${itemData.number}`);
